import ProductCard from "../ProductCard/ProductCard";
import "./RightPane.css";

const RightPane = ({headerText, buttonSymbol, butttonText, productCards, onButtonClicked, }) => {
    console.log(productCards);
    let addProduct = () => {
        onButtonClicked();
    }

    let onCardClicked = (idFromcard) => {
            console.log("card gekliked: " +idFromcard)
    }

    let productCardsToBeRenderd = productCards.map(product =>{
       if(product.name === "Placeholder"){


       return( <li  key={product.id} className="productList__Item">
        <button onClick={addProduct} className="productList__button">{buttonSymbol || "*"}</button>
        <p className="productList__text">{butttonText || "lorem Ipsum"}</p>
    </li>);
    }
    return  <ProductCard  onCardClicked={onCardClicked} key={product.id} name={product.name} id={product.id} productImg={product.img}/>
    ;

});

    return (
        <section className="productWrapper">
            <header className="header">
                <h1 class="header__h1">{headerText || "placeholder"}</h1>

            </header>
            <ul className="productList">
               {productCardsToBeRenderd}

            </ul>

        </section>
    );

}

export default RightPane;
