import LeftPane from "../LeftPane/LeftPane";
import RightPane from "../RightPane/Rightpane";
import "./Dashboard.css";
import treeImage from"../../img/tree.jpeg";
import suitcaseImage from"../../img/suitcase.webp";
import grassImage from "../../img/grass.jpeg"
import React from "react";
import { render } from "@testing-library/react";

class Dashboard extends React.Component{
    constructor(props){
        super(props)
        this.state = {productCards: []}
    }

    onButtonClicked = () =>{
        let toBEAdded = [
            {
                name: "Placeholder"
            }
        ]
        let mergedArrays = this.state.productCards.concat(toBEAdded);
        this.setState({
            productCards:  mergedArrays
    });
    }


    componentDidMount(){
        let productCards =
        [
            {
                name: "Placeholder",

            },
            {
                name: "Tree",
                img: treeImage
            },
            {
                name: "Suitcase",
                img: suitcaseImage
            },

        ]
        this.setState({productCards: productCards})
    };

        render(){
            let navigationListItems =
        [
            {
                name: "Home",
                message: 0,
            },
            {
                name: "Facturen",
                message: 0,
            },
            {
                name: "Bestellingen",
                message: 1,
            },
            {
                name: "Retour",
                message: 1,
            },
            {
                name: "Contact",
                message: 2,
            },
        ];
            return (<article className="dashboard">
            <LeftPane navigationListItems={navigationListItems} buttonText="Go Premium" />
            <RightPane onButtonClicked={this.onButtonClicked} productCards={this.state.productCards} headerText={"Mijn Producten"} buttonSymbol="+" butttonText={"Voeg product toe"} />
        </article>
        );
        }




}
export default Dashboard
