import LeftPane from "../LeftPane/LeftPane";
import RightPane from "../RightPane/Rightpane";
import "./Dashboard.css";
import Popup from "../Popup/Popup";
import treeImage from "../../img/tree.jpeg";
import suitcaseImage from "../../img/suitcase.webp";
import grassImage from "../../img/grass.jpeg"
import colaImage from "../../img/cola.webp"
import eagleImage from "../../img/eagle.webp"
import React from "react";
import { render } from "@testing-library/react";


class Dashboard extends React.Component {
    constructor(props) {
        super(props)
        this.state = { productCards: [], open: true }
    }


    addButtonClicked = (inputfromPopup) => {
        let toBeAddedImage;
        switch (inputfromPopup) {
            case ("Cola"):
                toBeAddedImage = colaImage;
                break;
            case ("Tree"):
                toBeAddedImage = treeImage;
                break;
            case("Suitcase"):
                toBeAddedImage = suitcaseImage;
                break;
            default: 
            toBeAddedImage = eagleImage
            break;
        }
        let toBEAdded = [
            {
                name: inputfromPopup,
                img: toBeAddedImage
            }
        ]
        let mergedArrays = this.state.productCards.concat(toBEAdded);
        this.setState({
            productCards: mergedArrays,
            open: !this.state.open,
        });
    }
    onButtonClicked = () => {
        this.setState({ open: !this.state.open })
    }


    componentDidMount() {
        let productCards =
            [
                {
                    name: "Placeholder",

                },
                {
                    name: "Tree",
                    img: treeImage
                },
                {
                    name: "Suitcase",
                    img: suitcaseImage
                },

            ]
        this.setState({ productCards: productCards })
    };

    render() {
        let navigationListItems =
            [
                {
                    name: "Home",
                    message: 0,
                },
                {
                    name: "Facturen",
                    message: 0,
                },
                {
                    name: "Bestellingen",
                    message: 1,
                },
                {
                    name: "Retour",
                    message: 1,
                },
                {
                    name: "Contact",
                    message: 2,
                },
            ];
        if (this.state.open === true) {
            return (<article className="dashboard">
                <LeftPane navigationListItems={navigationListItems} buttonText="Go Premium" />
                <RightPane onButtonClicked={this.onButtonClicked} productCards={this.state.productCards} headerText={"Mijn Producten"} buttonSymbol="+" butttonText={"Voeg product toe"} />
            </article>
            );
        };

        return (
            <Popup addButtonClicked={this.addButtonClicked} />
        )
    }



}
export default Dashboard
