import LeftPane from "../LeftPane/LeftPane";
import RightPane from "../RightPane/Rightpane";
import "./Dashboard.css";
import productsObject from "../../data/Products"

import Popup from "../Popup/Popup";
import chooseImage from "../../helpers/chooseImage";
import React from "react";



class Dashboard extends React.Component {
    constructor(props) {
        super(props)
        this.state = { productCards: [], open: true }
    }


    addButtonClicked = (inputfromPopup) => {
        let imageFromHelper = chooseImage(inputfromPopup);

        let toBEAdded = [
            {
                name: inputfromPopup,
                img: imageFromHelper
            }
        ]
        let mergedArrays = this.state.productCards.concat(toBEAdded);
        this.setState({
            productCards: mergedArrays,
            open: !this.state.open,
        });
    }
    onButtonClicked = () => {
        this.setState({ open: !this.state.open })
    }


    componentDidMount() {

        this.setState({productCards: productsObject.products})
    };

    render() {
        let navigationListItems =
            [
                {
                    name: "Home",
                    message: 0,
                },
                {
                    name: "Facturen",
                    message: 0,
                },
                {
                    name: "Bestellingen",
                    message: 1,
                },
                {
                    name: "Retour",
                    message: 1,
                },
                {
                    name: "Contact",
                    message: 2,
                },
            ];
        if (this.state.open === true) {
            return (<article className="dashboard">
                <LeftPane navigationListItems={navigationListItems} buttonText="Go Premium" />
                <RightPane onButtonClicked={this.onButtonClicked} productCards={this.state.productCards} headerText={"Mijn Producten"} buttonSymbol="+" butttonText={"Voeg product toe"} />
            </article>
            );
        };

        return (
            <Popup addButtonClicked={this.addButtonClicked} />
        )
    }



}
export default Dashboard
